import{_ as t,o as a,c as o,aA as n}from"./chunks/framework.69izd8vI.js";const u=JSON.parse('{"title":"Independent Component Analysis (ICA)","description":"","frontmatter":{},"headers":[],"relativePath":"explanations/ica.md","filePath":"explanations/ica.md","lastUpdated":null}'),i={name:"explanations/ica.md"};function r(s,e,l,p,c,h){return a(),o("div",null,[...e[0]||(e[0]=[n('<h1 id="Independent-Component-Analysis-ICA" tabindex="-1">Independent Component Analysis (ICA) <a class="header-anchor" href="#Independent-Component-Analysis-ICA" aria-label="Permalink to &quot;Independent Component Analysis (ICA) {#Independent-Component-Analysis-ICA}&quot;">​</a></h1><p>Understanding ICA for EEG artifact removal.</p><h2 id="What-is-ICA?" tabindex="-1">What is ICA? <a class="header-anchor" href="#What-is-ICA?" aria-label="Permalink to &quot;What is ICA? {#What-is-ICA?}&quot;">​</a></h2><p>Independent Component Analysis is a blind source separation technique that decomposes EEG signals into statistically independent components.</p><h2 id="Why-Use-ICA-for-EEG?" tabindex="-1">Why Use ICA for EEG? <a class="header-anchor" href="#Why-Use-ICA-for-EEG?" aria-label="Permalink to &quot;Why Use ICA for EEG? {#Why-Use-ICA-for-EEG?}&quot;">​</a></h2><p>ICA is particularly effective because:</p><ol><li><p><strong>Artifact sources are independent</strong>: Eye movements, heart beats, and brain activity have distinct patterns</p></li><li><p><strong>Non-invasive separation</strong>: Recovers sources without prior knowledge</p></li><li><p><strong>Preserves brain activity</strong>: Removes artifacts while keeping neural signals</p></li></ol><h2 id="How-ICA-Works" tabindex="-1">How ICA Works <a class="header-anchor" href="#How-ICA-Works" aria-label="Permalink to &quot;How ICA Works {#How-ICA-Works}&quot;">​</a></h2><blockquote><p><strong>TODO</strong>: Add mathematical explanation</p><ul><li><p>Independence assumption</p></li><li><p>Mixing model</p></li><li><p>Unmixing matrix</p></li></ul></blockquote><h2 id="Identifying-Artifact-Components" tabindex="-1">Identifying Artifact Components <a class="header-anchor" href="#Identifying-Artifact-Components" aria-label="Permalink to &quot;Identifying Artifact Components {#Identifying-Artifact-Components}&quot;">​</a></h2><h3 id="EOG-Eye-Movement-Components" tabindex="-1">EOG (Eye Movement) Components <a class="header-anchor" href="#EOG-Eye-Movement-Components" aria-label="Permalink to &quot;EOG (Eye Movement) Components {#EOG-Eye-Movement-Components}&quot;">​</a></h3><p>Characteristics:</p><ul><li><p>Strong frontal topography</p></li><li><p>Large amplitude</p></li><li><p>Stereotyped temporal patterns</p></li></ul><blockquote><p><strong>TODO</strong>: Add example images</p></blockquote><h3 id="ECG-Cardiac-Components" tabindex="-1">ECG (Cardiac) Components <a class="header-anchor" href="#ECG-Cardiac-Components" aria-label="Permalink to &quot;ECG (Cardiac) Components {#ECG-Cardiac-Components}&quot;">​</a></h3><p>Characteristics:</p><ul><li><p>Regular temporal pattern (~1 Hz)</p></li><li><p>Specific topography</p></li><li><p>High correlation with ECG channel</p></li></ul><h3 id="Muscle-Artifacts" tabindex="-1">Muscle Artifacts <a class="header-anchor" href="#Muscle-Artifacts" aria-label="Permalink to &quot;Muscle Artifacts {#Muscle-Artifacts}&quot;">​</a></h3><p>Characteristics:</p><ul><li><p>High-frequency content</p></li><li><p>Peripheral scalp distribution</p></li><li><p>Bursting temporal pattern</p></li></ul><h2 id="ICA-Algorithms" tabindex="-1">ICA Algorithms <a class="header-anchor" href="#ICA-Algorithms" aria-label="Permalink to &quot;ICA Algorithms {#ICA-Algorithms}&quot;">​</a></h2><blockquote><p><strong>TODO</strong>: Discuss different algorithms</p><ul><li><p>FastICA</p></li><li><p>Infomax</p></li><li><p>JADE</p></li><li><p>When to use each</p></li></ul></blockquote><h2 id="Best-Practices" tabindex="-1">Best Practices <a class="header-anchor" href="#Best-Practices" aria-label="Permalink to &quot;Best Practices {#Best-Practices}&quot;">​</a></h2><h3 id="Data-Requirements" tabindex="-1">Data Requirements <a class="header-anchor" href="#Data-Requirements" aria-label="Permalink to &quot;Data Requirements {#Data-Requirements}&quot;">​</a></h3><ul><li><p><strong>Length</strong>: At least 100,000 samples (better: 500,000+)</p></li><li><p><strong>Channels</strong>: More data points than channels</p></li><li><p><strong>Preprocessing</strong>: High-pass filter first (removes slow drifts)</p></li></ul><h3 id="Number-of-Components" tabindex="-1">Number of Components <a class="header-anchor" href="#Number-of-Components" aria-label="Permalink to &quot;Number of Components {#Number-of-Components}&quot;">​</a></h3><blockquote><p><strong>TODO</strong>: Guidance on component count selection</p></blockquote><h3 id="When-NOT-to-Use-ICA" tabindex="-1">When NOT to Use ICA <a class="header-anchor" href="#When-NOT-to-Use-ICA" aria-label="Permalink to &quot;When NOT to Use ICA {#When-NOT-to-Use-ICA}&quot;">​</a></h3><ul><li><p>Very short recordings</p></li><li><p>Few channels (&lt;20)</p></li><li><p>Data with discontinuities</p></li></ul><h2 id="Component-Rejection-vs.-Artifact-Correction" tabindex="-1">Component Rejection vs. Artifact Correction <a class="header-anchor" href="#Component-Rejection-vs.-Artifact-Correction" aria-label="Permalink to &quot;Component Rejection vs. Artifact Correction {#Component-Rejection-vs.-Artifact-Correction}&quot;">​</a></h2><blockquote><p><strong>TODO</strong>: Discuss trade-offs</p></blockquote><h2 id="See-Also" tabindex="-1">See Also <a class="header-anchor" href="#See-Also" aria-label="Permalink to &quot;See Also {#See-Also}&quot;">​</a></h2><ul><li><p><a href="./../tutorials/ica-workflow">ICA workflow tutorial</a></p></li><li><p><a href="./../reference/ica">ICA functions reference</a></p></li></ul>',33)])])}const m=t(i,[["render",r]]);export{u as __pageData,m as default};
